.gt-container.gt-input-focused:after {
  content: '';
  position: fixed;
  bottom: 0%;
  left: 0;
  right: 0;
  top: 0;
  background: #000;
  opacity: 0.6;
  -webkit-transition: opacity 0.3s, bottom 0s;
  transition: opacity 0.3s, bottom 0s;
  z-index: 9999;
