.banner {
  height: 100%;
  position: relative;
  overflow: hidden;
  cursor: default;
  overflow-wrap: break-word;
}
.tk-footer {
    display: none;
}
.banner .mask {
  position: absolute;
  width: 100%;
  height: 100%;
  background-color: rgba(0,0,0,0.3);
}
.banner .page-header {
  color: #fff;
  max-width: calc(960px - 6rem);
  width: 80%;
}
@media (max-width: 575px) {
  .banner .page-header,
  .banner .page-header .iconfont {
    font-size: 0.9rem;
  }
}
.banner[parallax="true"] {
  will-change: transform;
  -webkit-transform-style: preserve-3d;
  -webkit-backface-visibility: hidden;
}
@media (max-width: 100vh) {
  header {
    max-height: 100vw;
  }
  #board {
    margin-top: -1rem !important;
  }
}
@media (max-width: 79.99vh) {
  .scroll-down-bar {
    display: none;
  }
}
#board {
  position: relative;
  margin-top: -2rem;
  background-color: var(--board-bg-color);
  transition: background-color 0.2s ease-in-out;
  border-radius: 0.5rem;
  z-index: 3;
  -webkit-box-shadow: 0 12px 15px 0 rgba(0,0,0,0.24), 0 17px 50px 0 rgba(0,0,0,0.19);
  box-shadow: 0 12px 15px 0 rgba(0,0,0,0.24), 0 17px 50px 0 rgba(0,0,0,0.19);
}
.copy-btn {
  display: inline-block;
  cursor: pointer;
  border-radius: 0.1rem;
  border: none;
  background-color: transparent;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -webkit-appearance: none;
  font-size: 0.75rem;
  line-height: 1;
  font-weight: bold;
  outline: none;
  -webkit-transition: opacity 0.2s ease-in-out;
  -o-transition: opacity 0.2s ease-in-out;
  transition: opacity 0.2s ease-in-out;
  padding: 0.15rem;
  position: absolute;
  right: 0.35rem;
  top: 0.15rem;
  opacity: 0;
}
.copy-btn > i {
  font-size: 0.75rem;
  font-weight: 400;
}
.copy-btn > span {
  margin-left: 5px;
}
.copy-btn-dark {
  color: #6a737d;
}
.copy-btn-light {
  color: #bababa;
}
.markdown-body pre:hover > .copy-btn {
  opacity: 0.9;
}
.markdown-body pre:hover > .copy-btn,
.markdown-body pre:not(:hover) > .copy-btn {
  outline: none;
}
footer > div:not(:first-child) {
  margin: 0.25rem 0;
  font-size: 0.85rem;
}
footer .statistics {
  display: flex;
  flex-direction: row;
  justify-content: center;
}
footer .statistics > span {
  flex: 1;
  margin: 0 0.25rem;
}
footer .statistics > *:nth-last-child(2):first-child {
  text-align: right;
}
footer .statistics > *:nth-last-child(2):first-child ~ * {
  text-align: left;
}
footer .beian {
  display: flex;
  flex-direction: row;
  justify-content: center;
}
footer .beian > * {
  margin: 0 0.25rem;
}
footer .beian-police {
  position: relative;
  overflow: hidden;
  display: inline-flex;
  align-items: center;
  justify-content: left;
}
footer .beian-police img {
  margin-right: 3px;
  width: 1rem;
  height: 1rem;
  margin-bottom: 0.1rem;
}
@media (max-width: 424px) {
  footer .statistics {
    flex-direction: column;
  }
  footer .statistics > *:nth-last-child(2):first-child {
    text-align: center;
  }
  footer .statistics > *:nth-last-child(2):first-child ~ * {
    text-align: center;
  }
  footer .beian {
    flex-direction: column;
  }
  footer .beian .beian-police {
    justify-content: center;
  }
  footer .beian > *:nth-last-child(2):first-child {
    text-align: center;
  }
  footer .beian > *:nth-last-child(2):first-child ~ * {
    text-align: center;
  }
}
sup > a::before,
.footnote-text::before {
  display: block;
  content: "";
  margin-top: -5rem;
  height: 5rem;
  width: 1px;
  visibility: hidden;
}
sup > a::before,
.footnote-text::before {
  display: inline-block;
}
.footnote-item::before {
  display: block;
  content: "";
  margin-top: -5rem;
  height: 5rem;
  width: 1px;
  visibility: hidden;
}
.footnote-list ol {
  list-style-type: none;
  counter-reset: sectioncounter;
  padding-left: 0.5rem;
  font-size: 0.95rem;
}
.footnote-list ol li:before {
  font-family: "Helvetica Neue", monospace, "Monaco";
  content: "[" counter(sectioncounter) "]";
  counter-increment: sectioncounter;
}
.footnote-list ol li+li {
  margin-top: 0.5rem;
}
.footnote-text {
  padding-left: 0.5em;
}
@media (max-width: 767px) {
  #subtitle {
    font-size: 1.5rem;
  }
}
@media (max-width: 575px) {
  #subtitle {
    font-size: 1.35rem;
  }
}
.qr-trigger {
  cursor: pointer;
  position: relative;
}
.qr-trigger:hover .qr-img {
  display: block;
  transition: all 0.3s;
}
.qr-img {
  max-width: 12rem;
  position: absolute;
  right: -5.25rem;
  z-index: 99;
  display: none;
  border-radius: 0.2rem;
  background-color: transparent;
  box-shadow: 0 0 20px -5px rgba(158,158,158,0.2);
}
.scroll-down-bar {
  position: absolute;
  width: 100%;
  height: 6rem;
  text-align: center;
  cursor: pointer;
  bottom: 0;
}
.scroll-down-bar i {
  font-size: 2rem;
  font-weight: bold;
  display: inline-block;
  position: relative;
  padding-top: 2rem;
  color: #fff;
  -webkit-transform: translateZ(0);
  -moz-transform: translateZ(0);
  -ms-transform: translateZ(0);
  -o-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-animation: scroll-down 1.5s infinite;
  animation: scroll-down 1.5s infinite;
}
#scroll-top-button {
  position: fixed;
  z-index: 99;
  background: var(--board-bg-color);
  transition: background-color 0.2s ease-in-out, bottom 0.3s ease;
  border-radius: 4px;
  min-width: 40px;
  min-height: 40px;
  bottom: -60px;
  outline: none;
  display: flex;
  display: -webkit-flex;
  align-items: center;
  -webkit-box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 2px 10px 0 rgba(0,0,0,0.12);
  box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 2px 10px 0 rgba(0,0,0,0.12);
}
#scroll-top-button i {
  font-size: 32px;
  margin: auto;
  color: var(--sec-text-color);
}
#scroll-top-button:hover i,
#scroll-top-button:active i {
  -webkit-animation-name: scroll-top;
  animation-name: scroll-top;
  -webkit-animation-duration: 1s;
  animation-duration: 1s;
  -webkit-animation-delay: 0.1s;
  animation-delay: 0.1s;
  -webkit-animation-timing-function: ease-in-out;
  animation-timing-function: ease-in-out;
  -webkit-animation-iteration-count: infinite;
  animation-iteration-count: infinite;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
  -webkit-animation-direction: alternate;
  animation-direction: alternate;
}
#local-search-result .search-list-title {
  border-left: 3px solid #0d47a1;
}
#local-search-result .search-list-content {
  padding: 0 1.25rem;
}
#local-search-result .search-word {
  color: #ff4500;
}
html {
  font-size: 16px;
  letter-spacing: 0.02em;
}
html,
body {
  font-family: var(--font-family-sans-serif);
}
html,
body,
header {
  height: 100%;
  overflow-wrap: break-word;
}
body {
  transition: color 0.2s ease-in-out, background-color 0.2s ease-in-out;
  background-color: var(--body-bg-color);
  color: var(--text-color);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
body a {
  color: var(--text-color);
  text-decoration: none;
  cursor: pointer;
  transition: color 0.2s ease-in-out, background-color 0.2s ease-in-out;
}
body a:hover {
  color: var(--link-hover-color);
  text-decoration: none;
  transition: color 0.2s ease-in-out, background-color 0.2s ease-in-out;
}
code {
  color: inherit;
}
table {
  font-size: inherit;
  color: var(--post-text-color);
}
img[lazyload] {
  object-fit: cover;
}
*[align="left"] {
  text-align: left;
}
*[align="center"] {
  text-align: center;
}
*[align="right"] {
  text-align: right;
}
::-webkit-scrollbar {
  width: 6px;
  height: 4px;
}
::-webkit-scrollbar-thumb {
  background-color: var(--scrollbar-color);
  border-radius: 6px;
}
::-webkit-scrollbar-thumb:hover {
  background-color: var(--scrollbar-hover-color);
}
::-webkit-scrollbar-corner {
  background-color: transparent;
}
:root {
  --color-mode: 'light';
  --body-bg-color: #eee;
  --board-bg-color: #fff;
  --text-color: #3c4858;
  --sec-text-color: #718096;
  --post-text-color: #2c3e50;
  --post-heading-color: #1a202c;
  --post-link-color: #0366d6;
  --link-hover-color: #30a9de;
  --link-hover-bg-color: #f8f9fa;
  --navbar-bg-color: #2f4154;
  --navbar-text-color: #fff;
  --scrollbar-color: #c4c6c9;
  --scrollbar-hover-color: #a6a6a6;
}
@media (prefers-color-scheme: dark) {
  :root {
    --color-mode: 'dark';
  }
  :root:not([data-user-color-scheme]) {
    --body-bg-color: #181c27;
    --board-bg-color: #252d38;
    --text-color: #c4c6c9;
    --sec-text-color: #a7a9ad;
    --post-text-color: #c4c6c9;
    --post-heading-color: #c4c6c9;
    --post-link-color: #1589e9;
    --link-hover-color: #30a9de;
    --link-hover-bg-color: #364151;
    --navbar-bg-color: #1f3144;
    --navbar-text-color: #d0d0d0;
    --scrollbar-color: #687582;
    --scrollbar-hover-color: #9da8b3;
  }
  :root:not([data-user-color-scheme]) img,
  :root:not([data-user-color-scheme]) .note,
  :root:not([data-user-color-scheme]) .label {
    -webkit-filter: brightness(0.9);
    filter: brightness(0.9);
    transition: filter 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .page-header {
    color: #ddd;
    transition: color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body :not(pre) > code {
    background-color: rgba(62,75,94,0.35);
    transition: background-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body h1,
  :root:not([data-user-color-scheme]) .markdown-body h2 {
    border-bottom-color: #435266;
    transition: border-bottom-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body h1,
  :root:not([data-user-color-scheme]) .markdown-body h2,
  :root:not([data-user-color-scheme]) .markdown-body h3,
  :root:not([data-user-color-scheme]) .markdown-body h6,
  :root:not([data-user-color-scheme]) .markdown-body h5 {
    color: #ddd;
    transition: all 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body table tr {
    background-color: var(--board-bg-color);
    transition: background-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body table tr:nth-child(2n) {
    background-color: var(--board-bg-color);
    transition: background-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .markdown-body table th,
  :root:not([data-user-color-scheme]) .markdown-body table td {
    border-color: #435266;
    transition: border-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) hr,
  :root:not([data-user-color-scheme]) .markdown-body hr {
    border-top-color: #435266;
    transition: border-top-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .note :not(pre) > code {
    background-color: rgba(27,31,35,0.05);
  }
  :root:not([data-user-color-scheme]) .modal-dialog .modal-content .modal-header {
    border-bottom-color: #435266;
    transition: border-bottom-color 0.2s ease-in-out;
  }
  :root:not([data-user-color-scheme]) .gt-comment-admin .gt-comment-content {
    background-color: transparent;
    transition: background-color 0.2s ease-in-out;
  }
}
[data-user-color-scheme='dark'] {
  --body-bg-color: #181c27;
  --board-bg-color: #252d38;
  --text-color: #c4c6c9;
  --sec-text-color: #a7a9ad;
  --post-text-color: #c4c6c9;
  --post-heading-color: #c4c6c9;
  --post-link-color: #1589e9;
  --link-hover-color: #30a9de;
  --link-hover-bg-color: #364151;
  --navbar-bg-color: #1f3144;
  --navbar-text-color: #d0d0d0;
  --scrollbar-color: #687582;
  --scrollbar-hover-color: #9da8b3;
}
